import React, { useState, useContext, useEffect } from "react";
import { LoginContext } from "../App";

const LoginForm = () => {
  const { setUserData } = useContext(LoginContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");


  const updateData = (event) => {
    switch (event.target.name) {
      case "usersname":
        setUsername(event.target.value); 
        break;
      case "password":
        setPassword(event.target.value);  

      default:
        break;
    }
  };

  const loginPlayer = (event) => {
    event.preventDefault();

    if (playerName.length === 0) {
      setErrorPlayerName("Please enter your name");
    } else {
      setErrorPlayerName("");
    }

    if (playerName.length > 0) {
      setUserData({ playerName: playerName });

      setPlayerNameChanged(false);
    }
  };
  return (
    <>
      <div>
        <div className="Login_container">
          <div className="form_container">
            <div className="heading_container">
              <h2>Login</h2>
            </div>
            <div className="newForm">
              <form onSubmit={loginPlayer}>
                <div>
                 <label htmlFor="username">Username</label>
                 <input
                  name="username"
                  onChange={updateData}
                  value={username}
                 />
                 </div>
                 <div>
                  <label>Password</label>
                  <input name="password" onChange={updateData} value={password} />
                 </div>
                 <div className="button_container">
                  <button className="playButton" type="submit">
                    Play
                 </button>

                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginForm;
